import React, { useState, useEffect } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';
import './App.css';

function DigitalHuman() {
  const { scene, materials, textures, isLoading, error } = useGLTF('/models/girl.glb');

  useEffect(() => {
    if (error) {
      console.error('Error loading model:', error);
    }
    if (isLoading) {
      console.log('Loading model...');
    }
    if (scene) {
      console.log('Model loaded:', scene);
      console.log('Materials:', materials);
      console.log('Textures:', textures);
    }
  }, [scene, materials, textures, isLoading, error]);

  if (error) {
    return <p>Error loading model.</p>;
  }
  if (isLoading) {
    return <p>Loading model...</p>;
  }
  return <primitive object={scene} scale={0.5} position={[0, -1.5, 0]} />;
}

function App() {
  const [conversation, setConversation] = useState([]);
  const [loading, setLoading] = useState(false);
  const [input, setInput] = useState("");

  const handleUserInput = async () => {
    if (!input) return;
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ input }),
      });
      const data = await response.json();

      setConversation((prev) => [...prev, { user: input, bot: data.response }]);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <div className="digital-human">
        <Canvas>
          <ambientLight intensity={1} />
          <directionalLight position={[0, 5, 5]} />
          <DigitalHuman />
          <OrbitControls />
        </Canvas>
      </div>
      <div className="conversation">
        {conversation.map((msg, index) => (
          <div key={index}>
            <p>User: {msg.user}</p>
            <p>Bot: {msg.bot}</p>
          </div>
        ))}
        {loading && <p>Loading...</p>}
      </div>
      <div className="input-container">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === 'Enter') {
              handleUserInput();
            }
          }}
        />
        <button onClick={handleUserInput}>Send</button>
      </div>
    </div>
  );
}

export default App;
