import React, { useState, Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import './App.css';
import ErrorBoundary from './ErrorBoundary';
import VoiceInput from './VoiceInput';

const DigitalHuman = React.lazy(() => import('./DigitalHuman'));

function App() {
  const [conversation, setConversation] = useState([]);
  const [loading, setLoading] = useState(false);
  const [input, setInput] = useState("");

  const handleUserInput = async (inputText) => {
    if (!inputText) return;
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5001/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ input: inputText }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`Error: ${errorData.message} - ${JSON.stringify(errorData.error)}`);
      }

      const data = await response.json();
      setConversation((prev) => [...prev, { user: inputText, bot: data.response }]);
    } catch (error) {
      console.error('Error:', error);
      alert(`Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleInputSubmit = () => {
    handleUserInput(input);
    setInput("");
  };

  return (
    <div className="App">
      <div className="digital-human">
        <ErrorBoundary>
          <Suspense fallback={<p>Loading model...</p>}>
            <Canvas>
              <ambientLight intensity={1} />
              <directionalLight position={[0, 5, 5]} />
              <DigitalHuman />
              <OrbitControls />
            </Canvas>
          </Suspense>
        </ErrorBoundary>
      </div>
      <div className="conversation">
        {conversation.map((msg, index) => (
          <div key={index}>
            <p>User: {msg.user}</p>
            <p>Bot: {msg.bot}</p>
          </div>
        ))}
        {loading && <p>Loading...</p>}
      </div>
      <div className="input-container">
        <input
          type="text"
          value={input}
          onChange={handleInputChange}
          onKeyDown={(e) => {
            if (e.key === 'Enter') {
              handleInputSubmit();
            }
          }}
        />
        <button onClick={handleInputSubmit}>Send</button>
        <VoiceInput onInput={handleUserInput} />
      </div>
    </div>
  );
}

export default App;
